rule.definition:
load(
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: -1,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "Request_Id",
      ascending: true
    )
  ),
  local!selectedStatus,
  local!Request,
  with(
    local!datasubset: #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000ddbd-335c-8000-aae2-01ef9001ef90_6706",
      query: #"SYSTEM_SYSRULES_query"(
        aggregation: #"SYSTEM_SYSRULES_queryAggregation"(aggregationColumns: {
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "Request_Id", aggregationFunction: "COUNT"),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "Maintenance_Status", isGrouping: true),
        }),
        pagingInfo: local!pagingInfo
      )
    ),
    if(
      isnull(local!selectedStatus),
    #"SYSTEM_SYSRULES_pieChartField"(
      label: "Maintenance Status of vehicles in the process",
      series: {
        apply(
          #"SYSTEM_SYSRULES_chartSeries"(label: _, data: _,links: _),
          merge(
            index(local!datasubset.data, "Maintenance_Status", null),
            index(local!datasubset.data, "Request_Id", null),
            apply(
              #"SYSTEM_SYSRULES_dynamicLink"(value: _, saveInto: local!selectedStatus),
              index(local!datasubset.data, "Maintenance_Status",null)
              
            )
          )
        )
      }
     
    ),
    
     load(
        local!gridPagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
          startIndex: 1,
          batchSize: 20,
          sort: #"SYSTEM_SYSRULES_sortInfo"(
            field: "Request_Id",
            ascending: true
          )
        ),
        with(
          local!gridDatasubset: #"SYSTEM_SYSRULES_queryEntity"(
            entity: #"_a-0000ddbd-335c-8000-aae2-01ef9001ef90_6706",
            query: #"SYSTEM_SYSRULES_query"(
              selection: #"SYSTEM_SYSRULES_querySelection"(columns: {
                #"SYSTEM_SYSRULES_queryColumn"(field: "Request_Id"),
                #"SYSTEM_SYSRULES_queryColumn"(field: "Maintenance_Status")
              }),
              filter: #"SYSTEM_SYSRULES_queryFilter"(field: "Maintenance_Status", operator: "=", value: local!selectedStatus),
              pagingInfo: local!gridPagingInfo
            )
          ),
         
            {
            #"SYSTEM_SYSRULES_linkField"(
              labelPosition: "COLLAPSED",
              links: #"SYSTEM_SYSRULES_dynamicLink"(
                label: "Back to Chart",
                value: null,
                saveInto: {
                  local!selectedStatus,
                  /* Need to reset the paging info back to the first page when the user */
                  /* changes the filter. Otherwise, the grid could error out.           */
                  a!save(local!gridPagingInfo.startIndex, 1)
                }
              )
            ),
          #"SYSTEM_SYSRULES_gridField"(
            label: local!selectedStatus & " Status",
            totalCount: local!gridDatasubset.totalCount,
            columns: {
              #"SYSTEM_SYSRULES_gridTextColumn"(label: "Request Id", field: "Request_Id", data: index(local!gridDatasubset.data, "Request_Id", {})),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Customer Name", 
              field: "Customer_Name",
              data: apply(#"_a-0000ddce-5ecb-8000-aae2-01ef9001ef90_7965",index(local!gridDatasubset.data, "Request_Id", ""))
              
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Plate Number", 
              field: "Vehicle_Plate_Number",
              data: apply(#"_a-0000ddce-5ecb-8000-aae2-01ef9001ef90_7969",index(local!gridDatasubset.data, "Request_Id", ""))
              
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(label: "Maintenance_Status", field: "Maintenance_Status", data: index(local!gridDatasubset.data, "Maintenance_Status", {})),
              
              
              
              
            },
            value: local!gridPagingInfo,
            saveInto: local!gridPagingInfo
          )
            }
        )
      )
    )
  )
)
